#!/usr/bin/env node

/**
 * Simple test script for the Noir Playground Server
 * Run with: node test-server.js
 */

const BASE_URL = 'http://localhost:4000';

async function testServer() {
    console.log('🧪 Testing Noir Playground Server...\n');

    try {
        // Test 1: Health check
        console.log('1️⃣ Testing health check...');
        const healthResponse = await fetch(`${BASE_URL}/api/health`);
        const health = await healthResponse.json();
        console.log('✅ Health:', health.message);
        console.log('📅 Timestamp:', health.timestamp);
        console.log('');

        // Test 2: Check profiler availability
        console.log('2️⃣ Checking profiler availability...');
        const profilerResponse = await fetch(`${BASE_URL}/api/profile/check-profiler`);
        const profilerStatus = await profilerResponse.json();

        if (profilerStatus.available) {
            console.log('✅ Profiler available:', profilerStatus.version);
        } else {
            console.log('❌ Profiler not available:', profilerStatus.message);
            console.log('💡 Make sure noir-profiler is installed with: noirup');
            console.log('');
        }
        console.log('');

        // Test 3: Test profiling with JSON artifact
        console.log('3️⃣ Testing profiling with JSON artifact...');

        const sampleArtifact = { "noir_version": "1.0.0-beta.8+ba05d729b9753aa5ce2b076c1dd4795edb173f68", "hash": "13406761233232836770", "abi": { "parameters": [{ "name": "x", "type": { "kind": "field" }, "visibility": "private" }, { "name": "y", "type": { "kind": "field" }, "visibility": "public" }], "return_type": { "abi_type": { "kind": "field" }, "visibility": "public" }, "error_types": {} }, "bytecode": "", "debug_symbols": "", "file_map": { "50": { "source": "pub fn main(x: Field, y: pub Field) -> pub Field {\n    // Verify that x and y are both non-zero\n    assert(x != 0);\n    assert(y != 0);\n    \n    // Compute the sum and verify it's greater than both inputs\n    let sum = x + y;\n    assert(sum as u64 > x as u64);\n    assert(sum as u64 > y as u64);\n    \n    // Return the sum as proof output\n    sum\n}", "path": "/Users/ted/SuperData/working/research/aztec/hello_world/src/main.nr" } }, "names": ["main"], "brillig_names": ["directive_invert", "directive_integer_quotient"] };

        try {
                  const profileResponse = await fetch(`${BASE_URL}/api/profile/opcodes`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          artifact: sampleArtifact,
          sourceCode: `pub fn main(x: Field, y: pub Field) -> pub Field {
    // Verify that x and y are both non-zero
    assert(x != 0);
    assert(y != 0);
    
    // Compute the sum and verify it's greater than both inputs
    let sum = x + y;
    assert(sum as u64 > x as u64);
    assert(sum as u64 > y as u64);
    
    // Return the sum as proof output
    sum
}`,
          cargoToml: `[package]
name = "hello_world"
version = "0.1.0"
type = "bin"

[dependencies]
noir_std = { git = "https://github.com/noir-lang/noir", tag = "v0.25.0" }`
        })
      });

            const result = await profileResponse.json();

                  if (result.success) {
        console.log('✅ Profiling successful!');
        console.log(`📊 Generated ${result.svgs.length} SVG files:`);
        result.svgs.forEach(svg => {
          console.log(`   - ${svg.filename} (${svg.function}, ${svg.type})`);
          console.log(`     📏 SVG size: ${svg.content.length} characters`);
        });
        
        if (result.circuitMetrics) {
          console.log('📈 Circuit Statistics:');
          console.log(`   - Total ACIR Opcodes: ${result.circuitMetrics.totalAcirOpcodes}`);
          console.log(`   - Total Brillig Opcodes: ${result.circuitMetrics.totalBrilligOpcodes}`);
          console.log(`   - Total Gates: ${result.circuitMetrics.totalGates}`);
          console.log(`   - Functions: ${result.circuitMetrics.functions.length}`);
          result.circuitMetrics.functions.forEach(func => {
            console.log(`     • ${func.function}: ${func.acirOpcodes} ACIR, ${func.brilligOpcodes} Brillig`);
          });
        }
      } else {
        console.log('❌ Profiling failed:', result.error);
      }
        } catch (error) {
            console.log('❌ Profiling error:', error.message);
        }

    } catch (error) {
        console.error('❌ Server test failed:', error.message);
        console.log('💡 Make sure the server is running on port 4000');
    }
}

// Run tests
async function main() {
    await testServer();

    console.log('\n🎉 Test completed!');
    console.log('\n💡 Next steps:');
    console.log('   1. Install noir-profiler: noirup');
    console.log('   2. Build and run with Docker: docker-compose up --build');
    console.log('   3. Or run locally: npm run start:dev');
}

main();
